import React from 'react';
import './visualiser.css';

export default class Svisualiser extends React.Component {
    constructor(props){
        super(props);

        this.state = {
            array: [],
        };
    }
    componentDidMount(){
        this.resetArray();
    }

    resetArray() {
        const array = [];
        for (let i = 0; i<100; i++){
            array.push(randomizer(10,850));
        } 
        this.setState({array});
    }

    
    render(){
        const {array} = this.state;

        return (
            <div> 
            {array.map ((value, idx) => (
                <div
                  className="array-bar"
                  key={idx}>
                 {value} 
                </div> 
            ))  }
            </div>
        )
            }


}

function randomizer(minn, maxx) {
return Math.floor(Math.random() * (maxx - minn+1) + min);
}
